import React, { useState } from 'react';
import { FaPlus, FaSearch, FaCheck, FaTimes, FaQuestion, FaInfoCircle } from 'react-icons/fa';

interface SavedQuery {
  name: string;
  content: string;
  status: 'collected' | 'not collected';
  documentCount?: number;
}

interface Document {
  id: number;
  title: string;
  abstract: string;
  date: string;
  selected: boolean;
}

interface Criterion {
  id: number;
  description: string;
}

interface DocumentAnalysisProps {
  savedQueries: SavedQuery[];
}

const DocumentAnalysis: React.FC<DocumentAnalysisProps> = ({ savedQueries }) => {
  const [selectedQuery, setSelectedQuery] = useState<SavedQuery | null>(null);
  const [documents, setDocuments] = useState<Document[]>([]);
  const [criteria, setCriteria] = useState<Criterion[]>([]);
  const [newCriterion, setNewCriterion] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResults, setAnalysisResults] = useState<{[key: number]: {[key: number]: 'Yes' | 'No' | 'Uncertain'}}>({});
  const [filterDate, setFilterDate] = useState('');
  const [filterKeyword, setFilterKeyword] = useState('');
  const [tooltips, setTooltips] = useState<{[key: number]: {[key: number]: string}}>({});

  const handleQuerySelect = (query: SavedQuery) => {
    setSelectedQuery(query);
    // Simulate loading documents
    const mockDocuments: Document[] = [
      { id: 1, title: 'Impact of Exercise on Cardiovascular Health', abstract: 'This study examines the effects of regular exercise on heart health...', date: '2023-05-15', selected: false },
      { id: 2, title: 'Dietary Interventions for Hypertension Management', abstract: 'A comprehensive review of various dietary approaches to manage high blood pressure...', date: '2023-04-22', selected: false },
      { id: 3, title: 'Genetic Factors in Coronary Artery Disease', abstract: 'An analysis of genetic predispositions and their role in the development of coronary artery disease...', date: '2023-03-10', selected: false },
    ];
    setDocuments(mockDocuments);
  };

  const handleAddCriterion = () => {
    if (newCriterion.trim()) {
      setCriteria([...criteria, { id: criteria.length + 1, description: newCriterion.trim() }]);
      setNewCriterion('');
    }
  };

  const handleDocumentSelect = (id: number) => {
    setDocuments(documents.map(doc => doc.id === id ? { ...doc, selected: !doc.selected } : doc));
  };

  const handleAnalyzeDocuments = () => {
    setIsAnalyzing(true);
    // Simulate analysis process
    setTimeout(() => {
      const mockResults: {[key: number]: {[key: number]: 'Yes' | 'No' | 'Uncertain'}} = {};
      const mockTooltips: {[key: number]: {[key: number]: string}} = {};
      documents.forEach(doc => {
        mockResults[doc.id] = {};
        mockTooltips[doc.id] = {};
        criteria.forEach(criterion => {
          mockResults[doc.id][criterion.id] = ['Yes', 'No', 'Uncertain'][Math.floor(Math.random() * 3)] as 'Yes' | 'No' | 'Uncertain';
          mockTooltips[doc.id][criterion.id] = `Justification for Criteria ${criterion.id}: ${['Clear evidence found', 'No supporting data', 'Requires further investigation'][Math.floor(Math.random() * 3)]}`;
        });
      });
      setAnalysisResults(mockResults);
      setTooltips(mockTooltips);
      setIsAnalyzing(false);
    }, 3000);
  };

  const filteredDocuments = documents.filter(doc => 
    (!filterDate || doc.date >= filterDate) &&
    (!filterKeyword || doc.title.toLowerCase().includes(filterKeyword.toLowerCase()) || doc.abstract.toLowerCase().includes(filterKeyword.toLowerCase()))
  );

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4 text-teal-700">Document Analysis</h2>
      <select 
        className="w-full p-2 border border-gray-300 rounded-md mb-4"
        onChange={(e) => handleQuerySelect(savedQueries[parseInt(e.target.value)])}
        value={selectedQuery ? savedQueries.indexOf(selectedQuery).toString() : ''}
      >
        <option value="">Select a query</option>
        {savedQueries.map((query, index) => (
          <option key={index} value={index}>{query.name}</option>
        ))}
      </select>

      {selectedQuery && (
        <>
          <div className="mb-4">
            <h3 className="text-lg font-semibold mb-2 text-teal-700">Criteria Definition</h3>
            <div className="flex mb-2">
              <input
                type="text"
                value={newCriterion}
                onChange={(e) => setNewCriterion(e.target.value)}
                className="flex-grow p-2 border border-gray-300 rounded-l-md"
                placeholder="Enter new criterion"
              />
              <button
                onClick={handleAddCriterion}
                className="bg-teal-500 text-white p-2 rounded-r-md hover:bg-teal-600"
              >
                <FaPlus />
              </button>
            </div>
            <ul>
              {criteria.map(criterion => (
                <li key={criterion.id} className="mb-1">
                  <span className="font-bold text-green-600">Criteria {criterion.id}:</span> {criterion.description}
                </li>
              ))}
            </ul>
          </div>

          <div className="mb-4">
            <h3 className="text-lg font-semibold mb-2 text-teal-700">Document Filtering</h3>
            <input
              type="date"
              value={filterDate}
              onChange={(e) => setFilterDate(e.target.value)}
              className="p-2 border border-gray-300 rounded-md mr-2"
            />
            <input
              type="text"
              value={filterKeyword}
              onChange={(e) => setFilterKeyword(e.target.value)}
              className="p-2 border border-gray-300 rounded-md"
              placeholder="Search keyword"
            />
          </div>

          <div className="mb-4">
            <h3 className="text-lg font-semibold mb-2 text-teal-700">Documents</h3>
            {filteredDocuments.map(doc => (
              <div key={doc.id} className="border p-4 mb-4 rounded-md flex">
                <div className="flex-grow">
                  <div className="flex items-center justify-between mb-2">
                    <h4 className="font-semibold">{doc.title}</h4>
                    <input
                      type="checkbox"
                      checked={doc.selected}
                      onChange={() => handleDocumentSelect(doc.id)}
                    />
                  </div>
                  <p className="text-sm mb-2">{doc.abstract}</p>
                  <p className="text-xs text-gray-500">Published: {doc.date}</p>
                </div>
                {analysisResults[doc.id] && (
                  <div className="ml-4 border-l pl-4">
                    <h5 className="font-semibold mb-2">Criteria Selection</h5>
                    {criteria.map(criterion => (
                      <div key={criterion.id} className="flex items-center mb-1">
                        <span className="mr-2">Criteria {criterion.id}:</span>
                        {analysisResults[doc.id][criterion.id] === 'Yes' && <FaCheck className="text-green-500" />}
                        {analysisResults[doc.id][criterion.id] === 'No' && <FaTimes className="text-red-500" />}
                        {analysisResults[doc.id][criterion.id] === 'Uncertain' && <FaQuestion className="text-orange-500" />}
                        <div className="relative ml-2">
                          <FaInfoCircle className="text-blue-500 cursor-pointer" title={tooltips[doc.id][criterion.id]} />
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>

          <button
            onClick={handleAnalyzeDocuments}
            disabled={isAnalyzing || criteria.length === 0 || documents.filter(d => d.selected).length === 0}
            className="w-full bg-teal-500 text-white p-2 rounded-md hover:bg-teal-600 disabled:bg-gray-300"
          >
            {isAnalyzing ? 'Analyzing...' : 'Analyze Documents'}
          </button>

          {isAnalyzing && (
            <div className="mt-4 w-full bg-gray-200 rounded-full h-2.5 dark:bg-gray-700">
              <div className="bg-teal-600 h-2.5 rounded-full" style={{width: '45%'}}></div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default DocumentAnalysis;
