import React, { useState } from 'react';
import { FaCheck } from 'react-icons/fa';

interface QuestionConfirmationProps {
  onConfirm: () => void;
}

const QuestionConfirmation: React.FC<QuestionConfirmationProps> = ({ onConfirm }) => {
  const [answers, setAnswers] = useState<string[]>(['', '', '']);

  const questions = [
    'What is the primary outcome you are interested in?',
    'What population are you studying?',
    'Are there any specific interventions you want to focus on?',
  ];

  const handleAnswerChange = (index: number, value: string) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4 text-teal-700">Step 3: Confirm Topic Details</h2>
      {questions.map((question, index) => (
        <div key={index} className="mb-4">
          <label className="block text-sm font-medium text-teal-700">{question}</label>
          <input
            type="text"
            value={answers[index]}
            onChange={(e) => handleAnswerChange(index, e.target.value)}
            className="mt-1 w-full px-3 py-2 border border-teal-300 rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
          />
        </div>
      ))}
      <button
        onClick={onConfirm}
        className="px-4 py-2 bg-teal-500 text-white rounded-md hover:bg-teal-600 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-offset-2 flex items-center"
      >
        Confirm <FaCheck className="ml-2" />
      </button>
    </div>
  );
};

export default QuestionConfirmation;
