import React, { useState } from 'react';
import QuestionConfirmation from './QuestionConfirmation';
import SynonymList from './SynonymList';
import DocumentAnalysis from './DocumentAnalysis';
import { FaSearch, FaArrowRight, FaCheck, FaList, FaFileAlt, FaChartBar } from 'react-icons/fa';

interface SavedQuery {
  name: string;
  content: string;
  status: 'collected' | 'not collected';
  documentCount?: number;
}

const QueryGenerator: React.FC = () => {
  const [activeTab, setActiveTab] = useState('generate');
  const [step, setStep] = useState(1);
  const [queryName, setQueryName] = useState('');
  const [naturalLanguageQuery, setNaturalLanguageQuery] = useState('');
  const [pubMedQuery, setPubMedQuery] = useState('');
  const [savedQueries, setSavedQueries] = useState<SavedQuery[]>([]);

  const handleNextStep = () => {
    setStep(step + 1);
  };

  const handleQueryNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setQueryName(e.target.value);
  };

  const handleNaturalLanguageQueryChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setNaturalLanguageQuery(e.target.value);
  };

  const handlePubMedQueryChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setPubMedQuery(e.target.value);
  };

  const handleSaveQuery = () => {
    const newQuery: SavedQuery = {
      name: queryName,
      content: pubMedQuery,
      status: 'not collected'
    };
    setSavedQueries([...savedQueries, newQuery]);
    // Reset form
    setStep(1);
    setQueryName('');
    setNaturalLanguageQuery('');
    setPubMedQuery('');
  };

  const handleCollectDocuments = (index: number) => {
    const updatedQueries = [...savedQueries];
    const documentCount = Math.floor(Math.random() * 1000) + 1; // Simulate document collection
    updatedQueries[index] = {
      ...updatedQueries[index],
      status: 'collected',
      documentCount
    };
    setSavedQueries(updatedQueries);
  };

  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <div>
            <h2 className="text-xl font-semibold mb-4 text-teal-700">Step 1: Choose a query name</h2>
            <input
              type="text"
              value={queryName}
              onChange={handleQueryNameChange}
              className="w-full px-3 py-2 border border-teal-300 rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
              placeholder="Enter query name"
            />
            <button
              onClick={handleNextStep}
              className="mt-4 px-4 py-2 bg-teal-500 text-white rounded-md hover:bg-teal-600 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-offset-2 flex items-center"
            >
              Next <FaArrowRight className="ml-2" />
            </button>
          </div>
        );
      case 2:
        return (
          <div>
            <h2 className="text-xl font-semibold mb-4 text-teal-700">Step 2: Describe your request in natural language</h2>
            <textarea
              value={naturalLanguageQuery}
              onChange={handleNaturalLanguageQueryChange}
              className="w-full px-3 py-2 border border-teal-300 rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
              rows={4}
              placeholder="Describe your research question..."
            />
            <button
              onClick={handleNextStep}
              className="mt-4 px-4 py-2 bg-teal-500 text-white rounded-md hover:bg-teal-600 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-offset-2 flex items-center"
            >
              Next <FaArrowRight className="ml-2" />
            </button>
          </div>
        );
      case 3:
        return <QuestionConfirmation onConfirm={handleNextStep} />;
      case 4:
        return (
          <div>
            <h2 className="text-xl font-semibold mb-4 text-teal-700">Step 4: PubMed Query</h2>
            <textarea
              value={pubMedQuery}
              onChange={handlePubMedQueryChange}
              className="w-full px-3 py-2 border border-teal-300 rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
              rows={4}
              placeholder="Generated PubMed query..."
            />
            <SynonymList onSynonymClick={(synonym) => setPubMedQuery(pubMedQuery + ' ' + synonym)} />
            <button
              onClick={handleSaveQuery}
              className="mt-4 px-4 py-2 bg-teal-500 text-white rounded-md hover:bg-teal-600 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-offset-2 flex items-center"
            >
              Save Query <FaCheck className="ml-2" />
            </button>
          </div>
        );
      default:
        return <div className="text-teal-700 font-semibold">Query generation complete!</div>;
    }
  };

  const renderSavedQueries = () => (
    <div className="mt-8">
      <h2 className="text-xl font-semibold mb-4 text-teal-700 flex items-center">
        <FaList className="mr-2" /> Saved Queries
      </h2>
      <ul className="space-y-4">
        {savedQueries.map((query, index) => (
          <li key={index} className="border border-teal-200 rounded-md p-4">
            <h3 className="font-semibold text-teal-600">{query.name}</h3>
            <p className="text-sm text-gray-600 mt-1">{query.content}</p>
            <div className="mt-2 flex items-center justify-between">
              <span className={`text-sm ${query.status === 'collected' ? 'text-green-600' : 'text-yellow-600'}`}>
                Status: {query.status}
              </span>
              {query.status === 'collected' && (
                <span className="text-sm text-blue-600">
                  Documents: {query.documentCount}
                </span>
              )}
              {query.status === 'not collected' && (
                <button
                  onClick={() => handleCollectDocuments(index)}
                  className="px-3 py-1 bg-blue-500 text-white text-sm rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 flex items-center"
                >
                  <FaFileAlt className="mr-1" /> Collect Documents
                </button>
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );

  const renderTabs = () => (
    <div className="mb-4">
      <button
        className={`px-4 py-2 mr-2 rounded-t-lg ${activeTab === 'generate' ? 'bg-teal-500 text-white' : 'bg-gray-200'}`}
        onClick={() => setActiveTab('generate')}
      >
        Generate Query
      </button>
      <button
        className={`px-4 py-2 rounded-t-lg ${activeTab === 'analyze' ? 'bg-teal-500 text-white' : 'bg-gray-200'}`}
        onClick={() => setActiveTab('analyze')}
      >
        Analyze Documents
      </button>
    </div>
  );

  return (
    <div className="bg-white shadow-md rounded-lg p-6">
      <h1 className="text-2xl font-bold mb-6 text-teal-700 flex items-center">
        <FaSearch className="mr-2" /> Systematic Literature Review Assistant
      </h1>
      {renderTabs()}
      {activeTab === 'generate' ? (
        <>
          {renderStep()}
          {renderSavedQueries()}
        </>
      ) : (
        <DocumentAnalysis savedQueries={savedQueries.filter(q => q.status === 'collected')} />
      )}
    </div>
  );
};

export default QueryGenerator;
